def is_cross_mas(rowIdx,colIdx, all_letters):
    if rowIdx - 1 < 0 or rowIdx + 1 >  len(all_letters) - 1 or colIdx - 1 < 0 or colIdx + 1 > len(all_letters[0]) - 1:
        return False
    left_cross_ok = (all_letters[rowIdx][colIdx] == "A" and all_letters[rowIdx - 1][colIdx - 1] == "M" and all_letters[rowIdx + 1][colIdx + 1] == "S") or (all_letters[rowIdx][colIdx] == "A" and all_letters[rowIdx + 1][colIdx + 1] == "M" and all_letters[rowIdx - 1][colIdx - 1] == "S")
    right_cross_ok = (all_letters[rowIdx][colIdx] == "A" and all_letters[rowIdx - 1][colIdx + 1] == "M" and all_letters[rowIdx + 1][colIdx - 1] == "S") or ((all_letters[rowIdx][colIdx] == "A" and all_letters[rowIdx - 1][colIdx + 1] == "S" and all_letters[rowIdx + 1][colIdx - 1] == "M"))
    return left_cross_ok and right_cross_ok


combinations = 0
for rowIdx, row in enumerate(all_letters):
    for colIdx, el in enumerate(row):
        allowed_funcs = [is_cross_mas]
        # allowed_funcs = [xmas_right_to_left,xmas_left_to_right,xmas_bottom_to_top,xmas_top_to_bottom,xmas_right_diagonal_up,xmas_right_diagonal_down,xmas_left_diagonal_down,xmas_left_diagonal_up]
        is_allowed = False
        for func in allowed_funcs:
            if func(rowIdx, colIdx, all_letters) == True:
                print("func", func.__name__, f"row={rowIdx},col={colIdx}")
                combinations += 1
print(combinations)